// VerPerfil.js
import React, { useState, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../App'; 
import { useNavigate } from 'react-router-dom';

export function VerPerfil() {
  const { user, setUser } = useContext(UserContext);
  const [isEditing, setIsEditing] = useState(false);
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleInputChange = (event) => {
    setUser({
      ...user,
      [event.target.name]: event.target.value
    });
  };

  const handlePasswordChange = async (event) => {
    event.preventDefault();
    if (newPassword !== confirmPassword) {
      alert("New passwords do not match");
      return;
    }
    // Make a request to your server to update the user's password
    const response = await axios.put('http://localhost:5000/updatepassword', {
      id: user.id,
      currentPassword: currentPassword,
      newPassword: newPassword
    });
    if (response.status === 200) {
      console.log("Password updated");
      setCurrentPassword("");
      setNewPassword("");
      setConfirmPassword("");
    }
  };

  return (
    <div>
      <h1>Perfil</h1>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          <label>
            Nombre:
            <input type="text" name="nombre" value={user.nombre} onChange={handleInputChange} />
          </label>
          <label>
            Email:
            <input type="text" name="email" value={user.email} onChange={handleInputChange} />
          </label>
          <button type="submit">Guardar cambios</button>
        </form>
      ) : (
        <div>
          <p>Nombre: {user.nombre}</p>
          <p>Email: {user.email}</p>
          <button onClick={() => setIsEditing(true)}>Editar perfil</button>
        </div>
      )}
      <h2>Cambiar contraseña</h2>
      <form onSubmit={handlePasswordChange}>
        <label>
          Contraseña actual:
          <input type="password" value={currentPassword} onChange={(event) => setCurrentPassword(event.target.value)} />
        </label>
        <label>
          Nueva contraseña:
          <input type="password" value={newPassword} onChange={(event) => setNewPassword(event.target.value)} />
        </label>
        <label>
          Confirmar nueva contraseña:
          <input type="password" value={confirmPassword} onChange={(event) => setConfirmPassword(event.target.value)} />
        </label>
        <button type="submit">Cambiar contraseña</button>
      </form>
    </div>
  );
}
